package com.lwt.springboot;

import sun.misc.BASE64Decoder;

import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.OutputStream;
import java.util.UUID;

/**
 * @program: springboot
 * @description:
 * @author: liangwt
 * @create: 2020-01-08 10:11
 **/
public class APPTest {
    public static void saveUserImgBase64(String filePath, String fileName, String userImg) throws IOException{
        String rgex = "data:image/(.*?);base64";
        //去除base64图片的前缀
        userImg = userImg.replaceFirst("data:(.+?);base64,", "");
        BASE64Decoder decoder = new BASE64Decoder();
        //解码
        byte[] b = decoder.decodeBuffer(userImg);
        //处理数据
        for (int i = 0; i < b.length; i++) {
            if (b[i] < 0) {
                b[i] +=256;
            }
        }
        //判断文件价是否存在，不存在则创建
        File dirFile = new File(filePath);
        if(!dirFile.exists()) {
            dirFile.mkdirs();
        }
        OutputStream out = new FileOutputStream(filePath + fileName);
        out.write(b);
        out.flush();
        out.close();
    }
    public static void main(String[] args) {
        try {
            String fileName = UUID.randomUUID().toString() + "." + "jpg";
            saveUserImgBase64("D:/mnt/MDFilesPath/",fileName,"");
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

